package io.stacs.nav.drs.service.dao.po;import io.stacs.nav.drs.service.dao.ActionPO;import lombok.Getter;import lombok.Setter;import java.util.Date;/** * The type Policy po. * * @author tangfashuang  policy po */@Getter @Setter public class PolicyPO implements ActionPO {    /**     * policy ID     */    private String policyId;    /**     * policy name     */    private String policyName;    /**     * related domain ids     */    private String domainIds;    /**     * create time     */    private Date createTime;    /**     * the decision type for vote ,1.FULL_VOTE,2.ONE_VOTE,3.ASSIGN_NUM     */    private String decisionType;    /**     * rs vote pattern 1.SYNC 2.ASYNC     */    private String votePattern;    /**     * callback type of slave 1.ALL 2.SELF     */    private String callbackType;    /**     * the number to verify     */    private int verifyNum;    /**     * domain-ids that must be verified     */    private String mustDomainIds;    /**     * the expression for vote rule     * example: n/2+1     */    private String expression;    /**     * An authorized domain_ids collection is required for modification     */    private String requireAuthIds;    /**     * version of policy,default 0     */    private int version;    /**     * update time     */    private Date updateTime;}